<script context="module">
  export async function load({page, fetch}) {
    const res = await fetch( `../api/bird/${page.params.slug}`)

    if (res) {
			return {
				props: {
					bird: await res.json()
				}
			};
		}

    return {
			status: res.status,
			error: new Error().message,
		};
  }

</script>

